import { Table, message, Popconfirm } from "antd";
import React from "react";
import { useEffect, useState } from "react/cjs/react.production.min";



const Brand = ()=>{
 const 	columns = [
    {
        title: "Brand",
        dataIndex: "brand",
        key: "brand",
    },
    {
        title: "Country",
        dataIndex: "country",
        key: "country",
    },
    {
        title: "Description",
        dataIndex: "description",
        key: "Description",
    },
    {
        title: "",
        key: "action",
        render: (_text, record) => (
            <Popconfirm
                title="Are you sure delete this beer?"
                onConfirm={() => deleteBeer(record.id)}
                okText="Yes"
                cancelText="No"
            >
                <a href="#" type="danger">
                    Delete{" "}
                </a>
            </Popconfirm>
        ),
    },
];
const [brand,setBrand] = useState()

const loadBrand = () => {
    const url = "api/v1/brands";
    fetch(url)
        .then((data) => {
            if (data.ok) {
                return data.json();
            }
            throw new Error("Network error.");
        })
        .then((data) => {
            data.forEach((brand) => {
                const newEl = {
                    key: brand.id,
                    id: brand.id,
                    name:brand.name,
                    country:brand.country,
                    description:brand.description

                };

                setBrand(...brand, newEl);
            });
        })
        .catch((err) => message.error("Error: " + err));
};

useEffect(()=>{
    loadBrand();
},[])

    return (
        <>

        <Table
					className="table-striped-rows"
					dataSource={brand}
					columns={columns}
					pagination={{ pageSize: 5 }}
				/>

        </>
    )
}

export default Brand;
